import { randomBytes } from "crypto";
import { signWithRecovery } from "./lib";

while (true) {
    const msg = randomBytes(32);
    const key = randomBytes(32);
    const result = signWithRecovery(msg, key);
    if (result.recovery >= 3) {
        console.log(result.recovery, msg.toString("hex"), key.toString("hex"));
        break;
    }
}
