/* eslint-disable @typescript-eslint/no-unsafe-call */
/* eslint-disable @typescript-eslint/no-unsafe-member-access */
/* eslint-disable @typescript-eslint/no-unsafe-assignment */

import { BufferReader } from "@node-lightning/bufio";
import bech32 from "bech32";
import { ADDRESS_VERSION } from "./address-version";
import * as crypto from "./crypto";
import { FIELD_TYPE } from "./field-type";
import { hrpToPico } from "./hrp-pico";
import { Invoice } from "./invoice";
import { WordCursor } from "./word-cursor";

/**
 * Decodes an invoice into an Invoice object
 * @param invoice
 * @return
 */
export function decode(invoice: string): Invoice {
    // Decode the invoice into prefix and words.
    // The words will be interated over to decode the rest of thee invoice
    const { prefix, words } = bech32.decode(invoice, Number.MAX_SAFE_INTEGER);

    // Parse the prefix into the network and the value in pico bitcoin.
    const { network, picoBtc } = parsePrefix(prefix);

    // Construct a word cursor to read from the remaining data
    const wordcursor = new WordCursor(words);

    const timestamp = wordcursor.readUIntBE(7); // read 7 words / 35 bits

    const fields = [];
    const unknownFields = [];

    // read fields until at signature
    while (wordcursor.wordsRemaining > 104) {
        const type = wordcursor.readUIntBE(1); // read 1 word / 5 bits
        const len = wordcursor.readUIntBE(2); // read 2 words / 10 bits

        let value;

        switch (type) {
            case 0:
                continue; // read off padding
            case FIELD_TYPE.PAYMENT_HASH: // p - 256-bit sha256 payment_hash
                value = wordcursor.readBytes(len);
                // push non-standard length field into unknown fields
                if (len !== 52) {
                    unknownFields.push({ type, value });
                    continue;
                }
                break;
            case FIELD_TYPE.ROUTE: // r - variable, one or more entries containing extra routing info
                {
                    value = [];
                    const bytes = wordcursor.readBytes(len);
                    const bytecursor = new BufferReader(bytes);
                    while (!bytecursor.eof) {
                        value.push({
                            pubkey: bytecursor.readBytes(33),
                            short_channel_id: bytecursor.readBytes(8),
                            fee_base_msat: bytecursor.readUInt32BE(),
                            fee_proportional_millionths: bytecursor.readUInt32BE(),
                            cltv_expiry_delta: bytecursor.readUInt16BE(),
                        });
                    }
                }
                break;
            case FIELD_TYPE.EXPIRY: // x - expiry time in seconds
                value = wordcursor.readUIntBE(len);
                break;
            case FIELD_TYPE.FALLBACK_ADDRESS: // f - variable depending on version
                {
                    const version = wordcursor.readUIntBE(1);
                    const address = wordcursor.readBytes(len - 1);
                    value = {
                        version,
                        address,
                    };
                    if (
                        version !== ADDRESS_VERSION.SEGWIT &&
                        version !== ADDRESS_VERSION.P2PKH &&
                        version !== ADDRESS_VERSION.P2SH
                    ) {
                        unknownFields.push({ type, value });
                        continue;
                    }
                }
                break;
            case FIELD_TYPE.SHORT_DESC: // d - short description of purpose of payment utf-8
                value = wordcursor.readBytes(len).toString("utf8");
                break;
            case FIELD_TYPE.PAYEE_NODE: // n - 33-byte public key of the payee node
                value = wordcursor.readBytes(len);
                if (len !== 53) {
                    unknownFields.push({ type, value });
                    continue;
                }
                break;
            case FIELD_TYPE.HASH_DESC: // h - 256-bit sha256 description of purpose of payment
                value = wordcursor.readBytes(len);
                if (len !== 52) {
                    unknownFields.push({ type, value });
                    continue;
                }
                break;
            case FIELD_TYPE.MIN_FINAL_CLTV_EXPIRY: // c - min_final_cltv_expiry to use for the last HTLC in the route
                value = wordcursor.readUIntBE(len);
                break;
            default:
                value = wordcursor.readBytes(len);
                unknownFields.push({ type, value });
                continue;
        }

        fields.push({ type, value });
    }

    const sigBytes = wordcursor.readBytes(103); // read 512-bit sig
    const r = sigBytes.slice(0, 32);
    const s = sigBytes.slice(32);
    const recoveryFlag = wordcursor.readUIntBE(1);

    wordcursor.position = 0;
    let preHashData = wordcursor.readBytes(words.length - 104, true);
    preHashData = Buffer.concat([Buffer.from(prefix), preHashData]);
    const hashData = crypto.sha256(preHashData);

    // extract the pubkey for verifying the signature by either:
    // 1: using the payee field value (n)
    // 2: performing signature recovery
    const payeeNodeField = fields.find(p => p.type === FIELD_TYPE.PAYEE_NODE);
    const pubkey = payeeNodeField
        ? payeeNodeField.value // use payee node provided
        : crypto.ecdsaRecovery(hashData, sigBytes, recoveryFlag); // recovery pubkey from ecdsa sig

    // validate signature
    // note if we performed signature recovery this will always match
    // so we may want to just skip this if we had signature recovery
    if (!crypto.ecdsaVerify(pubkey, hashData, sigBytes)) throw new Error("Signature invalid");

    // constuct the invoice
    const result = new Invoice();
    result._value = picoBtc; // directly assign pico value since there is not setter
    result.network = network;
    result.timestamp = timestamp;
    result.fields = fields;
    result.unknownFields = unknownFields;
    result.signature = { r, s, recoveryFlag };
    result.pubkey = pubkey;
    result.hashData = hashData;
    result.usedSigRecovery = !!payeeNodeField;
    return result;
}

//////////////

/**
 * Parses the prefix into network and value and then performs
 * validations on the values.
 *
 * This code is rough. Should refactor into two steps:
 * 1) tokenize
 * 2) parse tokens
 *
 * Value is returned as pico bitcoin.
 */
function parsePrefix(prefix: string): { network: string; picoBtc: bigint } {
    if (!prefix.startsWith("ln")) throw new Error("Invalid prefix");
    let network = "";
    let tempValue = "";
    let value;
    let multiplier;
    let hasNetwork = false;
    let hasAmount = false;

    for (let i = 2; i < prefix.length; i++) {
        const charCode = prefix.charCodeAt(i);

        if (!hasNetwork) {
            if (charCode >= 97 && charCode <= 122) network += prefix[i];
            else hasNetwork = true;
        }

        if (hasNetwork && !hasAmount) {
            if (charCode >= 48 && charCode <= 57) tempValue += prefix[i];
            else if (tempValue) hasAmount = true;
            else throw new Error("Invalid amount");
        }

        if (hasAmount) {
            if (charCode >= 97 && charCode <= 122) multiplier = prefix[i];
            else throw new Error("Invalid character");
        }
    }

    // returns null if we do not have a value
    if (tempValue === "") value = null;
    // otherwise we multiply by the value by the pico amount to obtain
    // the actual pico value of the
    else value = BigInt(tempValue) * hrpToPico(multiplier);

    if (!isValidNetwork(network)) throw new Error("Invalid network");
    if (!isValidValue(value)) throw new Error("Invalid amount");

    return {
        network,
        picoBtc: value,
    };
}

function isValidNetwork(network) {
    return network === "bc" || network === "tb" || network === "bcrt" || network === "sb";
}

function isValidValue(value) {
    return value === null || value > 0;
}

export enum ADDRESS_VERSION {
    SEGWIT = 0,
    P2PKH = 17,
    P2SH = 18,
}

import * as crypto from "@node-lightning/crypto";

export function sha256(data: Buffer): Buffer {
    return crypto.sha256(data);
}

export function ecdsaSign(
    message: Buffer,
    privKey: Buffer,
): { signature: Buffer; recovery: number } {
    return crypto.signWithRecovery(message, privKey);
}

export function ecdsaRecovery(message: Buffer, signature: Buffer, recoveryFlag: number): Buffer {
    return crypto.recoverPubKey(signature, recoveryFlag, message, true);
}

export function ecdsaVerify(pubkey: Buffer, message: Buffer, signature: Buffer): boolean {
    return crypto.verifySig(message, signature, pubkey);
}

/**
 * Returns the pico equivalent of the hrp multiplier as a BN object
 *
 * For instance:
 *   since m (milli) is 0.001 bitcoin, this is the equivalent of
 *   1000000000 or 1e9 pico bitcoin.
 *
 * @param hrpMultiplier
 * @return returns the BN pico bitcoin value
 */
export function hrpToPico(hrpMultiplier: string): bigint {
    if (!hrpMultiplier) return BigInt(1e12);
    switch (hrpMultiplier) {
        case "m":
            return BigInt(1e9);
        case "u":
            return BigInt(1e6);
        case "n":
            return BigInt(1e3);
        case "p":
            return BigInt(1);
        default:
            throw new Error("Invalid multiplier");
    }
}

/* eslint-disable @typescript-eslint/no-unsafe-call */
/* eslint-disable @typescript-eslint/no-unsafe-member-access */
/* eslint-disable @typescript-eslint/no-unsafe-assignment */
/* eslint-disable @typescript-eslint/no-unsafe-return */

import bech32 from "bech32";
import bs58check from "bs58check";
import { ADDRESS_VERSION } from "./address-version";
import { EXPIRY_DEFAULT, MIN_FINAL_CLTV_EXPIRY_DEFAULT } from "./constants";
import * as crypto from "./crypto";
import { FallbackAddress } from "./fallback-address";
import { FIELD_TYPE } from "./field-type";
import { Route } from "./route";
import { Signature } from "./signature";

const picoToMsat = BigInt(10);
const picoToSat = picoToMsat * BigInt(1000);
const picoToBtc = BigInt(1e12);
const MAX_SHORT_DESC_BYTES = 639;

/**
 * Invoice is the state container used for invoice data. It is used
 * when building an invoice or contains the results from decoded
 * invoices. The Invoice type does not perform validation on
 * data it contains but does contain helper methods to help
 * construct proper invoices.
 */
export class Invoice {
    public network: string;
    public timestamp: number = 0;
    public fields: any[] = [];
    public unknownFields: any[] = [];

    /**
     * ECDSA signature used to sign the invoice.
     */
    public signature: Signature;

    /**
     * Compressed public key on elliptic curve secp256k1 corresponding
     * to the node that generated and signed the invoice. Returned
     * as 33-bytes.
     */
    public pubkey: Buffer;

    /**
     * Buffer containing the buffer of the data used to generate the
     * hash used in the signature.
     */
    public hashData: Buffer;

    /**
     * Inidicates if signature recovery was used when decoding and
     * performing signature verification for an invoice. This value
     * will be false if a payee node field was provided.
     */
    public usedSigRecovery: boolean;

    /**
     * Invoice value stored in pico bitcoin
     */
    public _value: bigint;

    /**
     * Returns true when the invoice has a value
     * associated with it. Invoices may optionally contain a value.
     * When there is no value, the invoice is for the receipt of
     * any value.
     */
    public get hasValue(): boolean {
        return typeof this._value === "bigint";
    }

    /**
     * Warning: there is the possibility of precision loss!
     *
     * Gets the value in bitcoin as a string by converting from pico btc
     * into bitcoin. Returns null if the invoice has no amount.
     *
     * Sets the value from a number or string that represeents a bitcoin
     * value, such as 0.0001 to represent 10000 satoshi. Setting a falsy
     * value will remove the value from the invoice.
     *
     * @deprecated This property is maintained for backwards compaibility.
     * Use property `valueSat` or `valueMsat` instead.
     */
    public get amount(): string {
        return this.hasValue ? (Number(this._value) / Number(picoToBtc)).toFixed(11) : null;
    }

    public set amount(val) {
        if (!val) this._value = null;
        else this._value = BigInt(Math.trunc(parseFloat(val) * Number(picoToBtc)));
    }

    /**
     * Warning: Msat fractions are truncated!
     *
     * Gets the value in satoshi as a string by converting from pico btc
     * into satoshi. Returns null if the invoice has no amount.
     *
     * Sets the value in satoshi from a string or number, such as 10000 satoshi.
     * Setting a falsy value will remove the value from the invoice.
     */
    public get valueSat() {
        return this.hasValue ? (this._value / picoToSat).toString() : null;
    }

    public set valueSat(val) {
        if (!val) this._value = null;
        else this._value = BigInt(val) * picoToSat;
    }

    /**
     * Gets the value in milli-sataoshi as a string or returns null
     * if the invoice has no amount.
     *
     * Sets the value in millisatoshi from a string or number. Setting a falsy
     * value will remove the value from the invoice.
     */
    public get valueMsat(): string {
        return this.hasValue ? (this._value / picoToMsat).toString() : null;
    }

    public set valueMsat(val) {
        if (!val || Number(val) === 0) this._value = null;
        else this._value = BigInt(val) * picoToMsat;
    }

    /**
     * Get the expiry time for the invoice as a big endian number
     * of seconds. The defualt is one hour (3600).
     *
     * Sets the expiry time in seconds for the invoice.  Only a single
     * expiry field is valid in the invoice.
     */
    public get expiry(): number {
        return this._getFieldValue(FIELD_TYPE.EXPIRY, EXPIRY_DEFAULT);
    }

    public set expiry(value) {
        this._setFieldValue(FIELD_TYPE.EXPIRY, value);
    }

    /**
     * Gets the 256-bit payment hash. The preimage of this value
     * will provide proof of payment.
     *
     * Sets the 256-bit payment hash for the invoice from a Buffer
     * or hex-encoded string. Only a single field of this type is
     * valid in the invoice.
     */
    public get paymentHash(): Buffer {
        return this._getFieldValue(FIELD_TYPE.PAYMENT_HASH);
    }

    public set paymentHash(value) {
        if (typeof value === "string") value = Buffer.from(value, "hex");
        this._setFieldValue(FIELD_TYPE.PAYMENT_HASH, value);
    }

    /**
     * Gets the description as either a shortDesc or hashDesc
     * value. If it is the former it is returned as a string.
     * hashDesc is returned as a buffer of the hash.
     *
     * Sets the description for the invoice. An invoice must use hash
     * description for messages longer than 639 bytes. If the string is
     * longer than 639 bytes, the description will be hashed and stored
     * in hashDesc. Otherwise, the raw string will be stored in the
     * short desc.
     */
    public get desc(): string | Buffer {
        return this.shortDesc || this.hashDesc;
    }

    public set desc(desc) {
        const len = Buffer.byteLength(desc);
        if (len > MAX_SHORT_DESC_BYTES) this.hashDesc = crypto.sha256(desc as Buffer);
        else this.shortDesc = desc as string;
    }

    /**
     * Gets the short description text. Returns null when the invoice
     * does not contain a short description. An invoice must set
     * either a short description or a hash description.
     *
     * Sets the short description text. Maximum valid length is 639
     * bytes. Only a single short desc or hash desc field is allowed.
     * Setting this field will remove the hashDesc field value.
     */
    public get shortDesc(): string {
        return this._getFieldValue(FIELD_TYPE.SHORT_DESC);
    }

    public set shortDesc(value) {
        this._removeFieldByType(FIELD_TYPE.HASH_DESC);
        this._setFieldValue(FIELD_TYPE.SHORT_DESC, value);
    }

    /**
     * Gets the 256-bit hash of the description. Returns
     * null when an invoice does not contain a hash description.
     * An invoice must contain either a shortDesc or hashDesc.
     *
     * Sets the hash description to the hex-encoded string or
     * Buffer containing the the hashed description.
     * This must be used for descriptions that are over 639 bytes
     * long. Setting this field will remove any short desc fields.
     */
    public get hashDesc(): Buffer {
        return this._getFieldValue(FIELD_TYPE.HASH_DESC);
    }

    public set hashDesc(value) {
        if (typeof value === "string") value = Buffer.from(value, "hex");
        this._removeFieldByType(FIELD_TYPE.SHORT_DESC);
        this._setFieldValue(FIELD_TYPE.HASH_DESC, value);
    }

    /**
     * Gets the 33-byte public key of the payee node. This is
     * used to explicitly describe the payee node instead of
     * relying on pub key recovery from the signature.
     *
     * Sets the 33-byte public key of the payee node. This is
     * used to set the public key explicitly instead of relying
     * on signature recovery. This field must match the pubkey
     * used to generate the signature.
     */
    public get payeeNode(): Buffer {
        return this._getFieldValue(FIELD_TYPE.PAYEE_NODE);
    }

    public set payeeNode(value) {
        if (typeof value === "string") value = Buffer.from(value, "hex");
        this._setFieldValue(FIELD_TYPE.PAYEE_NODE, value);
    }

    /**
     * Gets the min final route CLTV expiry. If none is provided,
     * the default is 9.
     *
     * Sets the min final route CLTV expiry used in the final route.
     */
    public get minFinalCltvExpiry(): number {
        return this._getFieldValue(FIELD_TYPE.MIN_FINAL_CLTV_EXPIRY, MIN_FINAL_CLTV_EXPIRY_DEFAULT);
    }

    public set minFinalCltvExpiry(value) {
        this._setFieldValue(FIELD_TYPE.MIN_FINAL_CLTV_EXPIRY, value);
    }

    /**
     * Gets a list of fall back addresses. An invoice can include
     * multiple fallback addresses to send to an on-chain address
     * in the event of failure.
     */
    public get fallbackAddresses(): FallbackAddress[] {
        return this.fields
            .filter(p => p.type === FIELD_TYPE.FALLBACK_ADDRESS)
            .map(p => p.value) as FallbackAddress[];
    }

    /**
     * Adds a fallback address to the invoice. An invoice can include
     * one or more fallback addresses to send to an on-chain address
     * in the event of failure. This field may not make sense for small
     * or time-sensitive payments.
     *
     * The address string will be parsed and the appropriate address
     * type is added to the field metadata. The address will be
     * converted into a buffer containing the string values of the
     * address.
     */
    public addFallbackAddress(addrStr: string) {
        let version: ADDRESS_VERSION;
        let address: number[] | Buffer;

        // TODO - externalize magic strings!!!
        if (addrStr.startsWith("1") || addrStr.startsWith("m") || addrStr.startsWith("n")) {
            version = ADDRESS_VERSION.P2PKH;
            address = bs58check.decode(addrStr).slice(1); // remove prefix
        } else if (addrStr.startsWith("3") || addrStr.startsWith("2")) {
            version = ADDRESS_VERSION.P2SH;
            address = bs58check.decode(addrStr).slice(1); // remove prefix
        } else if (addrStr.startsWith("bc1") || addrStr.startsWith("tb1")) {
            const words = bech32.decode(addrStr).words;
            version = words[0];
            address = bech32.fromWords(words.slice(1));
        }
        if (Array.isArray(address)) address = Buffer.from(address);
        this.fields.push({ type: FIELD_TYPE.FALLBACK_ADDRESS, value: { version, address } });
    }

    /**
     * Gets the list of routes that are specified in the invoice.
     * Route information is necessary to route payments to private
     * nodes.
     */
    public get routes(): Route[] {
        return this.fields.filter(p => p.type === FIELD_TYPE.ROUTE).map(p => p.value);
    }

    /**
     * Adds a collection of routes to the invoice. A route entry must
     * be provided by private nodes so that a public addressible node
     * can be found by the recipient.
     *
     * Multiple route fields can be added to an invoice in according
     * with BOLT 11 to give the routing options.
     */
    public addRoute(routes: Route[]) {
        for (const route of routes) {
            if (typeof route.pubkey === "string") route.pubkey = Buffer.from(route.pubkey, "hex");
            if (typeof route.short_channel_id === "string") {
                route.short_channel_id = Buffer.from(route.short_channel_id, "hex");
            }
        }
        this.fields.push({ type: FIELD_TYPE.ROUTE, value: routes });
    }

    ///////////////////////////////////////////////////////////////////

    /**
     * Gets the value of thee first matching field that matches the field
     * type. If no result is found, the default value will be used.
     */
    private _getFieldValue<T>(type: FIELD_TYPE, def?: T): T {
        const field = this.fields.find(p => p.type === type);
        return (field ? field.value : def) as T;
    }

    /**
     * Sets the field value for the first matching field. If no
     * field exists it will insert a new field with the type and value
     * supplied.
     */
    private _setFieldValue<T>(type: FIELD_TYPE, value: T) {
        const field = this.fields.find(p => p.type === type);
        if (field) field.value = value;
        else this.fields.push({ type, value });
    }

    /**
     * Removes the fields that match the supplied type.
     */
    private _removeFieldByType(type: FIELD_TYPE) {
        this.fields = this.fields.filter(p => p.type !== type);
    }
}

import * as bech32Util from "./bech32-util";

export class WordCursor {
    public words: number[];
    public position: number;

    constructor(words: number[] = []) {
        this.words = words;
        this.position = 0;
    }

    public get wordsRemaining(): number {
        return this.words.length - this.position;
    }

    public writeUIntBE(val: number, wordLen: number) {
        if (!wordLen) throw new Error("wordLen must be provided");
        const words = new Array(wordLen);
        const maxV = (1 << 5) - 1;
        for (let i = wordLen - 1; i >= 0; i--) {
            words[i] = val & maxV;
            val >>= 5;
        }
        this._merge(words);
    }

    public writeBytes(buf: Buffer, pad: boolean = true) {
        const words = bech32Util.convertWords(buf, 8, 5, pad);
        this._merge(words);
    }

    public readUIntBE(numWords: number) {
        const words = this.words.slice(this.position, this.position + numWords);
        let val = 0;
        for (const word of words) {
            val <<= 5;
            val |= word;
        }
        this.position += numWords;
        return val;
    }

    public readBytes(numWords: number, pad: boolean = false) {
        const words = this.words.slice(this.position, this.position + numWords);
        this.position += numWords;
        return Buffer.from(bech32Util.convertWords(words, 5, 8, pad));
    }

    private _merge(words: number[]) {
        this.words = this.words.concat(words);
    }
}

/**
 * Converts a Buffer into a a word array and optionally pads bits.
 * This function is needed because the default bech32 fromWords/toWords
 * functions do not allow us to optionally include/exclude padding.
 * The encoder needs to pad bits.  The decoder does not care about the
 * padding bits.
 *
 * @param data input data
 * @param inBits number of bits in, usually 5 or 8
 * @param outBits number of bits out, usualy 8 or 5
 * @param pad indicates if output should be padded
 * @return converted words
 */
export function convertWords(
    data: number[] | Buffer,
    inBits: number,
    outBits: number,
    pad: boolean,
): number[] {
    let value = 0;
    let bits = 0;
    const maxV = (1 << outBits) - 1;

    const result: number[] = [];
    for (let i = 0; i < data.length; ++i) {
        value = (value << inBits) | data[i];
        bits += inBits;

        while (bits >= outBits) {
            bits -= outBits;
            result.push((value >> bits) & maxV);
        }
    }

    if (pad && bits > 0) {
        result.push((value << (outBits - bits)) & maxV);
    }

    return result;
}

/**
 * Calculates the number of words needed to store the number.
 * In bech32, each word is 5 bits (2^5 = 32). To properly
 * encode a number, we need to determine how many words
 * are necessary to encode the value.
 *
 * @param num integer value
 * @returns number of words
 */
export function sizeofNum(num: number): number {
    return Math.ceil(Math.log2(num) / 5);
}

/**
 * Calculates the number of words needed to store
 * the supplied number of bits. In bech32, each words is 5 bits
 * (2^5 = 32).
 *
 * @param bits number of bits
 * @returns number of words
 */
export function sizeofBits(bits: number): number {
    return Math.ceil(bits / 5);
}

/**
 * Calculates the number of words needeed to store
 * the supplied number of bytes. In bech32, each words is 5 bits
 * (2^5 = 32). We first convert the bytes to bits and then
 * convert to words.
 *
 * @param bytes
 * @returns number of words
 */
export function sizeofBytes(bytes: number): number {
    return sizeofBits(bytes * 8);
}

import { hrpToPico } from "./hrp-pico";

/**
 * Encodes pico btc to human readable part amount. The hrp amount is defined
 * as the amount of bitcoin as a positive decimal integer with no leading 0s.
 * It is an optional number followed by an optional multiplier letter.
 *
 * The multipliers are:
 * m (milli)   0.001 bitcoin
 * u (micro)   0.000001 bitcoin
 * n (nano)    0.000000001 bitcoin
 * p (pico)    0.000000000001 bitcoin
 *
 * This means that 1msat is 10p and 1sat is 10n.
 */
export function encodePico(pico: string): string {
    if (!pico) return;
    const lsdIndex = _lsdIndex(pico);
    const hrpMultiplier = _minimumHrpMultiplier(lsdIndex);
    return (BigInt(pico) / hrpToPico(hrpMultiplier)).toString() + hrpMultiplier;
}

///////////////////////////////////////////////////////////////////////////////

/**
 * Finds the index of the least significant digit
 *
 * For example:
 *   if the value is 1002 pico, the first digit with a value is 0
 *   if the value is 1040 pico, the first digit with a value is 1
 *   if the value is 1000 pico, the first digit with a value is 3
 *
 * @return the index the least significant digit
 */
function _lsdIndex(msatStr: string): number {
    for (let i = msatStr.length - 1; i >= 0; i--) {
        if (Number(msatStr[i]) !== 0) return msatStr.length - i - 1;
    }
    throw new Error("Cannot encode 0");
}

/**
 * With the least significant digit we can determine the multiplier
 * that is required to miniimze the HRP.
 *
 * For instance
 *   if the value is 1002, we have a value of p
 *   if the value is 1000, we have a value of n
 *
 * @param lsdIndex
 * @return returns the string multiplier
 */
function _minimumHrpMultiplier(lsdIndex: number): string {
    if (lsdIndex < 3) return "p";
    else if (lsdIndex < 6) return "n";
    else if (lsdIndex < 9) return "u";
    else if (lsdIndex < 12) return "m";
    else return "";
}

